import React, { useState, useRef } from "react";
import { Input } from "antd";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import toast, { Toaster } from "react-hot-toast";

const ProductLayout = () => {
	const [formData, setFormData] = useState({
		Name: "",
		Bio: "",
		Brand: "",
		Price: "",
		Quantity: "",
		Size: [],
		Color: [],
		Image: [],
	});
	const [imgs, setImgs] = useState([]);
	const [colors, setColors] = useState([]);
	const imgRef = useRef(null);
	const queryClient = useQueryClient();

	// Mutation to add a shoe
	const { mutate: addShoe } = useMutation({
		mutationFn: async (formData) => {
			const res = await fetch("/api/shoes/add", {
				method: "POST",
				headers: { "Content-Type": "application/json" },
				body: JSON.stringify(formData),
			});
			const data = await res.json();
			if (!res.ok) throw new Error(data.error || "Failed to create Shoes");
			return data;
		},
		onSuccess: () => {
			setFormData({
				Name: "",
				Bio: "",
				Brand: "",
				Price: "",
				Quantity: "",
				Size: [],
				Color: [],
				Image: [],
			});
			queryClient.invalidateQueries(["product"]);
			closeModal();
		},
		onError: (error) => {
			toast.error(error.message);
		},
	});

	// Query to get all products
	const { data: product, isLoading } = useQuery({
		queryKey: ["product"],
		queryFn: async () => {
			const response = await fetch("/api/shoes/all");
			const data = await response.json();
			if (!response.ok) throw new Error(data.message || "Something went wrong!");
			return data;
		},
	});

	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setFormData((prevData) => ({ ...prevData, [name]: value }));
	};

	const handleSubmit = () => {
		formData.Image = imgs;
		formData.Color = colors;
		addShoe(formData);
	};

	const closeModal = () => {
		const modal = document.getElementById("Add_Car");
		if (modal) modal.close();
	};

	return (
		<div>
			<Toaster />
			<div className="p-12 w-full justify-end flex">
				<div
					className="bg-neutral-600 h-12 rounded-xl font-bold cursor-pointer hover:scale-105 transition-all ease-in-out duration-200 w-[120px] text-white p-3 hover:bg-white hover:text-black"
					onClick={() => document.getElementById("Add_Car").showModal()}
				>
					Add product
				</div>
			</div>

			<dialog id="Add_Car" className="modal">
				<div className="flex z-50 absolute justify-center items-center w-full">
					<div className="bg-gradient-to-r from-neutral-800 to-orange-800 rounded-xl w-[800px] p-5">
						<div className="text-white grid-cols-[15%_85%] grid text-xl gap-y-6 overflow-auto px-12">
							<p>Name:</p>
							<Input name="Name" value={formData.Name} onChange={handleInputChange} className="text-black" />

							<p>Bio:</p>
							<Input name="Bio" value={formData.Bio} onChange={handleInputChange} className="text-black" />

							<p>Brand:</p>
							<Input name="Brand" value={formData.Brand} onChange={handleInputChange} className="text-black" />

							<p>Price:</p>
							<Input name="Price" value={formData.Price} onChange={handleInputChange} className="text-black" />

							<p>Quantity:</p>
							<Input name="Quantity" value={formData.Quantity} onChange={handleInputChange} className="text-black" />

							{/* Additional fields for Size, Color, Image can be added similarly */}
						</div>
					</div>
				</div>
				<form method="dialog" className="modal-backdrop w-full absolute h-screen">
					<button className="">Close</button>
				</form>
			</dialog>
		</div>
	);
};

export default ProductLayout;
