import React, { useState } from 'react'

const ProductLayout = () => {

  const [isopen, setIsOpen] = useState(false);

  const handleOpen  = () => {
    setIsOpen(true)
  }
  const handleClose = () => {
    setIsOpen(false)
  }

  return (
    <div>
        <div className='p-12 w-full justify-end flex'a>
              <div className='bg-neutral-600 h-12 rounded-xl font-bold cursor-pointer hover:scale-105 transition-all ease-in-out duration-200 w-[120px] text-white p-3 hover:bg-white hover:text-black'
              onClick={handleOpen}
              >
                Add product
              </div>
        </div>

        {isopen && (
            <div className='fixed z-50 flex justify-center items-center h-[700px] w-full'>
                 <div className='bg-neutral-700 h-[900px] p-5'>
                 <div 
                  onClick={handleClose}
                  className='bg-red-500 p-2 cursor-pointer'
                  >
                      close
                  </div>
                  <div>

                  </div>
                 </div>
                  
            </div>
        )};
    </div>
  )
}

export default ProductLayout
