import React, { useState } from "react";
import { Input } from "antd";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useEffect, useRef } from "react";
import toast, { Toaster } from "react-hot-toast";

const ProductLayout = () => {
	const [formData, setFormData] = useState({
		Name: "",
		Bio: "",
		Brand: "",
		Price: "",
		Quantity: "",
		Size: [],
		Color: [],
		Image: [],
	});

	const [imgs, setImgs] = useState([]);
	const [colors, setColors] = useState([]);

	const imgRef = useRef(null);
	const queryClient = useQueryClient();
	/////////////////////////////////////////////////////////////////////////////////
	const {
		mutate: addShose,
		isError,
		error,
		isPending,
	} = useMutation({
		mutation: async (formData) => {
			try {
				const res = await fetch("/api/shoes/add", {
					method: "POST",
					headers: {
						"content-Type": "application/json",
					},
					body: JSON.stringify(formData),
				});

				const data = await res.json();
				if (!res.ok)
					throw new Error(data.error || "Failed to create Shoes");
				console.log(data);
				return data;
			} catch (error) {
				throw new Error(error.message);
			}
		},
		onSuccess: () => {
			setFormData({
				Name: "",
				Bio: "",
				Brand: "",
				Price: "",
				Quantity: "",
				Size: [],
				Color: [],
				Image: [],
			});
			closeModal();


			queryClient.invalidateQueries(["Product"]);
		},
		onError: (error) => {
			toast.error(error.message);
		},
	});

	///////////////////////////////////////////////
	//  getALLProduct
	const {
		data: product,
		isloading,
		refetch,
		isRefetching,
	} = useQuery({
		queryKey: ["product"],
		queryFn: async () => {
			try {
				const response = await fetch("/api/shoes/all");
				const data = await response.json();

				console.log(data);

				if (!response.ok) {
					throw new Error(data.message || "Something went wrong!");
				}

				return data;
			} catch (error) {
				throw new Error(error);
			}
		},
	});
	//////////////////////////////////////////////////
	const handleInputChange = (e) => {
		setFormData({ ...formData, [e.target.name]: e.target.value });
	};

	const handleSubmit = () => {
		// Send formData to the server
		formData.Image = imgs;
		formData.Color = colors;
		console.log(formData);
		addShose(formData);
	};
	////////////////////////////////////////////////
	const handleImgChange = (e) => {
		const files = Array.from(e.target.files);
		const updatedImgs = [...imgs];

		files.forEach((file) => {
			const reader = new FileReader();
			reader.onload = () => {
				updatedImgs.push(reader.result);
				// setImgs(updatedImgs);
				setImgs((prevImgs) => [...prevImgs, reader.result]);
			};
			reader.readAsDataURL(file);
		});
	};

	const handleRemoveImg = (indexToRemove) => {
		setImgs(imgs.filter((_, index) => index !== indexToRemove));
	};
	//////////////////////////////////////

	// open div add
	const closeModal = () => {
		const modal = document.getElementById("Add_Car");
		if (modal) {
			modal.close();
		}
	};
	return (
		<div>
			<div className="p-12 w-full justify-end flex">
				<button
					className="bg-neutral-600 h-12 rounded-xl font-bold cursor-pointer hover:scale-105 transition-all ease-in-out duration-200 w-[120px] text-white p-3 hover:bg-white hover:text-black"
					onClick={() =>
						document.getElementById("Add_Car").showModal()
					}
				>
					Add product
				</button>
			</div>
			<dialog id="Add_Car" className="modal">
					<div className="modal-box backdrop-blur-3xl bg-gray-700 shadow-gray-500 shadow-md bg-opacity-0 w-full h-full flex ">
						<div className=" rounded-lg shadow-lg w-full">
							<h2 className="text-xl text-white px-3">
								<textarea
									className="textarea textarea-bordered h-[10px] w-full"
									placeholder="Bio"
									name="bio"
									value={formData.bio}
									onChange={handleInputChange}
								></textarea>
							</h2>
							<h2 className="text-xl text-white p-3 grid grid-cols-2 gap-2">
								<Toaster
									position="top-center"
									reverseOrder={false}
								/>
								<textarea
									className="textarea textarea-bordered h-[10px]"
									placeholder="Brand"
									name="brand"
									value={formData.brand}
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered  h-[10px]"
									placeholder="Model"
									name="car_model"
									value={formData.car_model}
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered  h-[10px]"
									placeholder="Production year"
									name="production_year"
									value={formData.production_year}
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered  h-[10px]"
									placeholder="Body style"
									name="body_style"
									value={formData.body_style}
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered  h-[10px]"
									placeholder="Engine"
									name="engine"
									value={formData.engine}
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered h-[10px]"
									placeholder="Transmission"
									name="transmission"
									value={formData.transmission}
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered  h-[10px]"
									placeholder="Drive type"
									name="drive_type"
									value={formData.drive_type}
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered h-[10px]"
									placeholder="Fuel type"
									name="fuel_type"
									value={formData.fuel_type}
									onChange={handleInputChange}
								></textarea>

								<button
									variant="outline"
									className="bg-black border-none h-[48px]"
									onClick={() =>
										document
											.getElementById("Add_Performance")
											.showModal()
									}
								>
									<div className="w-full flex justify-start text-gray-400">
										Performance
									</div>
								</button>

								<dialog
									id="Add_Performance"
									className="modal "
								>
									<div className="w-[500px] bg-gray-700 shadow-white	 shadow-md rounded-xl p-3 bg-opacity-50 backdrop-blur-xl relative top-10">
										<div className="grid gap-4">
											<div className="grid gap-2">
												<div className="grid grid-cols-3 items-center gap-4">
													<label htmlFor="width">
														Horse Power
													</label>
													<Input
														id="width"
														defaultValue=""
														className="col-span-2 h-8"
														name="horsepower"
														value={
															formData.horsepower
														}
														onChange={
															handleInputChange
														}
													/>
												</div>
												<div className="grid grid-cols-3 items-center gap-4">
													<label htmlFor="maxWidth">
														Torque
													</label>
													<Input
														id="maxWidth"
														defaultValue=""
														className="col-span-2 h-8"
														name="torque"
														value={formData.torque}
														onChange={
															handleInputChange
														}
													/>
												</div>
												<div className="grid grid-cols-3 items-center gap-4">
													<label htmlFor="height">
														Top Speed
													</label>
													<Input
														id="height"
														defaultValue=""
														className="col-span-2 h-8"
														name="top_speed"
														value={
															formData.top_speed
														}
														onChange={
															handleInputChange
														}
													/>
												</div>
												<div className="grid grid-cols-3 items-center gap-4">
													<label htmlFor="maxHeight">
														Acceleration
													</label>
													<Input
														id="maxHeight"
														defaultValue=""
														className="col-span-2 h-8"
														name="acceleration"
														value={
															formData.acceleration
														}
														onChange={
															handleInputChange
														}
													/>
												</div>
											</div>
										</div>
									</div>
									<form
										method="dialog"
										className="modal-backdrop w-full absolute h-screen"
									>
										<button className="">
                                            Close
                                        </button>
									</form>
								</dialog>

								<textarea
									className="textarea textarea-bordered h-[10px]"
									placeholder="Seat capacity"
									name="seat_capacity"
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered h-[10px]"
									placeholder="Cargo capacity"
									name="cargo_space"
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered h-[10px]"
									placeholder="Audio system"
									name="audio_system"
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered h-[10px]"
									placeholder="Price"
									name="price"
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered h-[10px]"
									placeholder="Quantity"
									name="quantity"
									onChange={handleInputChange}
								></textarea>
								<textarea
									className="textarea textarea-bordered h-[10px]"
									placeholder="Warranty"
									name="warranty"
									onChange={handleInputChange}
								></textarea>
							</h2>
							<div className="w-full bg-black p-4 h-[200px] rounded-2xl bg-opacity-20">
				
									<div className="flex space-x-3">
										{imgs.map((img, index) => (
											<div>
												
												<img
													key={index}
													src={img}
													alt={`img-${index}`}
													className="w-auto h-20 object-cover rounded-xl"
												/>
											</div>
										))}
									</div>
								
								

								<div className="flex justify-between border-t py-2 border-t-gray-700">
									<div className="flex gap-1 items-center">
										
									</div>

									<input
										type="file"
										hidden
										ref={imgRef}
										onChange={handleImgChange}
										accept="image/*"
										multiple
									/>
								</div>
							</div>

							<div className="flex items-center">
								<div className="mt-4 flex w-full justify-end">
								
								</div>
							</div>
						</div>
					</div>
					<form method="dialog" className="modal-backdrop">
						<button className="outline-none">Close</button>
					</form>
				</dialog>
			

		</div>
	);
};

export default ProductLayout;
